from flask import Flask
from werkzeug.middleware.proxy_fix import ProxyFix

from app import log
from app.exts import oauth, login, socketio
from app.blueprints import blueprints

# GENERAL GUIDELINES:
# Only catch errors generated by faulty user input. If there is an error in our own API or database, we crash.
# Invalid requests from the frontend only get feedback if they are honest mistakes from unsuspecting users.


def init_exts(app):
    app.wsgi_app = ProxyFix(app.wsgi_app)
    oauth.init_app(app)
    for server in app.config["OAUTH_SERVERS"]:
        oauth.register(server)
    login.init_app(app)
    socketio.init_app(app)


def register_blueprints(app):
    for blueprint in blueprints:
        app.register_blueprint(blueprint)


def create_app(config_files):
    app = Flask(__name__)
    for file in config_files:
        app.config.from_pyfile(file)
    log.setup()
    init_exts(app)
    register_blueprints(app)
    # socketio.run(app)
    return app
